import {
  ADD_PRODUCT_TO_BASKET,
  REMOVE_PRODUCT_FROM_BASKET,
  CLEAN_BASKET
} from '$actionTypes';

export const addProductToBasket = item => (dispatch, getState) => {
  console.log('ADD TO LOCAL STORAGE', {...getState().basket.products, item});
  dispatch({
    type: ADD_PRODUCT_TO_BASKET,
    payload: item
  });
}

export const cleanBasket = () => {
  return {
    type: CLEAN_BASKET
  }
}

export const removeProductFromBasket = (id) => {
  return {
    type: REMOVE_PRODUCT_FROM_BASKET,
    payload: id
  }
}