import {
  PHONE_FETCH_BY_ID_START,
  PHONE_FETCH_BY_ID_FAIL,
  PHONE_FETCH_BY_ID_SUCCESS

} from '$actionTypes'

import { fetchPhoneById as fetchPhoneByIdApi } from '$api/index';

export const fetchPhoneById = (id) => async dispatch => {
  dispatch({ type: PHONE_FETCH_BY_ID_START })
  try {
    let phone = await fetchPhoneByIdApi(id);
    dispatch({
      type: PHONE_FETCH_BY_ID_SUCCESS,
      payload: phone
    })
  } catch (error) {
    dispatch({
      type: PHONE_FETCH_BY_ID_FAIL,
      payload: error,
      error: true
    })
  }
}