import React, { Component } from 'react';

import withModal from '$hoc/Modal'

import style from './ConfirmModal.module.css'


class ConfirmModal extends Component {

  componentDidMount() {
    this.props._onOpen();
    this.props.pushNavPanelItemsState([]);
  }
  componentWillUnmount() {
    this.props.popNavPanelItemsState();
  }
  

  render() {
    return (
      <div className={style.wrapper}>
        <div className={style.contentWrapper}>
          <h4>{this.props.text}</h4>
          <div className={style.sep}/>
          <div className={style.buttons}>
            <div onClick={() => {this.props.onConfirm && this.props.onConfirm(); this.props._closeModal()}}>OK</div>
            <div onClick={this.props._closeModal}>Cancel</div>
          </div>
        </div>
      </div>
    );
  }

}

export default withModal(ConfirmModal);