import React from 'react';

import Image from '$components/Image';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { Link } from 'react-router-dom';

import style from './PhonePage.module.css';

const PhoneCard = ({ toCart, imageLoaded, id, categoryId, name, description, price, image, ...rest }) => {
  let params = [];
  for (let k in rest) {
    params.push([k, rest[k]]);
  }
  return (
    <div className={style.cardWrapper}>
      <Image 
        className={style.cardImage}
        src={image}
      >
        <div></div>
      </Image>
      <div className={style.params}>
        <p style={{marginBottom: '15px'}}><span className={style.paramName}>price:</span><span>{`$${price}`}</span></p>
        {
          params.map(([key, value], i) => {
            return (
              <p key={i}><span className={style.paramName}>{key}:</span><span>{value}</span></p>
            );
          })
        }
        <div style={{margin: '30px 0 0'}}>
          <div 
            className={`${style.btn} ${style.toCart}`}
            onClick={toCart}  
          >
            Add to cart
            <FontAwesomeIcon style={{marginLeft: '7px'}} icon="shopping-cart"/>
          </div>
          <Link to="/" className={`${style.btn}`}>Back to store</Link>
        </div>
      </div>
      <div className={style.cardDescription}>
        <h3>{name}</h3>
        <p>{description}</p>
      </div>
    </div>
  );
};

export default PhoneCard;