import {
  PHONE_FETCH_BY_ID_START,
  PHONE_FETCH_BY_ID_SUCCESS,
  PHONE_FETCH_BY_ID_FAIL
} from '$actionTypes';

const initialState = {
  item: null,
  fetching: false,
  error: null
};

export default (state = initialState, {type, payload}) => {
  let prevState = {...state};
  switch (type) {
    case PHONE_FETCH_BY_ID_START:
      prevState.fetching = true;
      prevState.error = null;
      break;
      
    case PHONE_FETCH_BY_ID_SUCCESS:  
      prevState.item = payload
      prevState.fetching = false;
      break;
    
    case PHONE_FETCH_BY_ID_FAIL:
      prevState.item = null;
      prevState.error = payload;
      break;
    
    default:
      return state
  }

  return prevState;
}